package LeetCode;

import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.atomic.AtomicReference;

public class FindTheDifference389 {

    public static char findTheDifference(String s, String t) {

        Map<Character, Integer> sMap = new HashMap<>();
        Map<Character, Integer> tMap = new HashMap<>();
        AtomicReference<Character> addedLetter = new AtomicReference<>((char) 0);

        if (s.length() == 0)
            return t.charAt(0);

        for (int i = 0; i < t.length(); i++) {
            if (i != t.length() - 1) {
                sMap.put(s.charAt(i), sMap.getOrDefault(s.charAt(i), 0) + 1);
                tMap.put(t.charAt(i), tMap.getOrDefault(t.charAt(i), 0) + 1);
            } else {
                tMap.put(t.charAt(i), tMap.getOrDefault(t.charAt(i), 0) + 1);
            }
        }

        tMap.forEach((key, value) -> {
            if (tMap.get(key) != sMap.get(key))
                addedLetter.set(key);
        });

        return addedLetter.get();
    }

    public static void main(String[] args) {

        System.out.println(findTheDifference("abcd", "abcde")); // e
        System.out.println(findTheDifference("zzzzz", "zzzzzz")); // z
        System.out.println(findTheDifference("", "y")); // y
        System.out.println(findTheDifference("ab", "bab")); // b
        System.out.println(findTheDifference("ab", "acb")); // c
        System.out.println(findTheDifference(""
                , "")); // c
    }
}
